import React, {Component} from 'react';
import {quizzes, users} from '../examples';


class Question extends Component {

  render() {
    console.log(this.state.current);
    const tt = this.props.question.txtAnswers.map((txt, index) =>

      <label key={index}>
        {txt}
        <input type='checkbox' />
      </label>
    );

    return (
      
      <div>
        <h3>{this.props.question.question}</h3>
        <form>
          {tt}
          {this.props.question.imgAnswers.map((img, index) =>
          <>
            <img key={index} src={img} />
            <input type='checkbox' />
          </>
          )}
          <button onClick={this.props.nextQuestion}>Validate</button>
        </form>
      </div>
    );
  }
}

class Play extends Component {

  constructor(props) {
    super(props);
    this.quizz = quizzes.filter(q=> q._uid == this.props.match.params.id)[0];
    this.state = {
      current : 0
    };
  }

  nextQuestion() {
    const c = this.state.current+1;
    this.setState({current: c});
  }

  render() {
    return (
      <div>
        <h1>Playing</h1>
        <h2>{this.quizz.name}</h2>
        <Question question = {this.quizz.questions[this.state.current]} nextQuestion = {() => this.nextQuestion()} />
      </div>
    );
  }
}

export {Play};
