import React, {Component} from 'react';
import {quizzes, users} from '../examples';


class Question extends Component {

  render() {
    return (
      <div>
        <h3>{this.props.question.question}</h3>
        { console.log(this.props.question.txtAnswers) }
        {this.props.question.txtAnswers.map((txt, index) =>
          <p key={index}>{txt}</p>
        )}
      </div>
    );
  }
}

class Play extends Component {

  constructor(props) {
    super(props);
    this.quizz = quizzes.filter(q=> q._uid == this.props.match.params.id)[0];
    this.state = {
      current : 0
    };
  }

  render() {
    return (
      <div>
        <h1>Playing</h1>
        <h2>{this.quizz.name}</h2>
        <Question question = {this.quizz.questions[this.state.current]} />
      </div>
    );
  }
}

export {Play};
