import React, {Component} from 'react';

import {quizzes, users} from './examples';
import {HTTP_SERVER_PORT_PICTURES} from './constants.js';

import Accueil from './components/accueil.js';
import {ListQuiz} from './components/listofquiz';
import {Play} from './components/playing';
import {Menuroute} from './components/menuroute';
import {BrowserRouter, Switch, Route} from "react-router-dom";



class App extends Component {

  constructor(props) {
    super(props)
    this.state = {
      toggle: false,
      searching: false
    }
  }

    render() {
      return (
        <>
          <ListQuiz />
          <Play />
          <Menuroute />

          <BrowserRouter>
         <div>
           <Switch>
             <Route exact={true} path="/" component={Accueil} />
             <Route exact={true} path="/" component={Accueil} />
             <Route path="*" component={() => <p>Page Not Found</p>} />
           </Switch>
         </div>
       </BrowserRouter>

          <div id="navigation-mobile-wrapper" className=
            { this.state.searching ? "searching" : "" }>
            <button
              className="home"
              onClick={
                () => this.setState({
                  toggle: this.state.toggle ? false : true
                })
              }
            >
              { this.state.toggle ? <img src='img/menu-opened.svg' alt='Menu' /> : <img src='img/menu-closed.svg' alt='Menu' /> }
            </button>
            <form>
              <div className="wrapper">
                <input type="text" className="searchBar"/>
                <button type="submit">
                  <img src="img/search.svg" alt="Search" />
                </button>
              </div>
            </form>
            <button className=
            { this.state.toggle ? "link left" : "link" }>A</button>
            <button className=
            { this.state.toggle ? "link top" : "link" }>B</button>
            <button className=
            { this.state.toggle ? "link right" : "link" }>C</button>
            <nav id="navigation-mobile">

              <section className="left">
                <img src="img/home.svg" alt="Home" />
              </section>

              <section className="center">
              </section>

              <section className="right">
                <button onClick={
                  () => this.setState({
                    searching: this.state.searching ? false : true
                  })
                }>
                  <img src="img/search.svg" alt="Search" />
                </button>
              </section>

            </nav>
          </div>
        </>
      );
    }
}

export default App;
